python3 -c "
import os
import json

def find_largest_file(root_path, exclude_dirs=['.git']):
    largest_file = {'path': '', 'size': 0}

    for dirpath, dirnames, filenames in os.walk(root_path):
        # Remove excluded directories from the search
        dirnames[:] = [d for d in dirnames if d not in exclude_dirs]

        for filename in filenames:
            filepath = os.path.join(dirpath, filename)
            try:
                size = os.path.getsize(filepath)
                if size > largest_file['size']:
                    largest_file = {'path': filepath, 'size': size}
            except (OSError, IOError):
                continue

    return largest_file

result = find_largest_file('.')
# Convert to relative path from current directory
print(json.dumps(result, indent=2))
"
