import os
import json

def find_largest_file(start_path, exclude_dirs=['.git']):
    largest_file = {'path': '', 'size': 0}

    for root, dirs, files in os.walk(start_path):
        # Remove excluded directories from traversal
        dirs[:] = [d for d in dirs if d not in exclude_dirs]

        for file in files:
            file_path = os.path.join(root, file)
            try:
                file_size = os.path.getsize(file_path)
                if file_size > largest_file['size']:
                    largest_file = {
                        'path': file_path,
                        'size': file_size
                    }
            except (OSError, IOError):
                pass

    return largest_file

# Find largest file in current directory (.)
largest = find_largest_file('.')
print(f\"Path: {largest['path']}\")
print(f\"Size: {largest['size']} bytes\")
print(f\"Relative path: {largest['path'][2:]}\")  # Remove './' prefix
"
"}

