#!/usr/bin/env python3
import os
import json

def count_pytest_occurrences():
    """Count occurrences of 'pytest' (case-insensitive) across the repo."""
    count = 0
    excluded_dirs = {'.git', 'eval_artifacts'}
    max_file_size = 1_000_000  # 1 MB

    for root, dirs, files in os.walk('.'):
        # Remove excluded directories from traversal
        dirs[:] = [d for d in dirs if d not in excluded_dirs]

        for filename in files:
            filepath = os.path.join(root, filename)

            # Check file size
            try:
                file_size = os.path.getsize(filepath)
                if file_size > max_file_size:
                    continue
            except OSError:
                continue

            # Read file as bytes
            try:
                with open(filepath, 'rb') as f:
                    content = f.read()
            except (OSError, IOError):
                continue

            # Skip files with NUL bytes
            if b'\x00' in content:
                continue

            # Decode as UTF-8 with errors='ignore'
            try:
                text = content.decode('utf-8', errors='ignore')
            except UnicodeDecodeError:
                continue

            # Count occurrences of 'pytest' (case-insensitive)
            count += text.lower().count('pytest')

    return count

if __name__ == '__main__':
    result = count_pytest_occurrences()

    # Write result to answer.json
    os.makedirs('eval_artifacts', exist_ok=True)
    with open('eval_artifacts/answer.json', 'w') as f:
        json.dump({'token_pytest_count': result}, f)

    print(f"Total 'pytest' occurrences: {result}")
