#!/usr/bin/env python3
import os

def count_pytest_occurrences():
    """Count occurrences of 'pytest' (case-insensitive) across the repo."""
    exclude_dirs = {'.git', 'eval_artifacts'}
    total_count = 0

    for root, dirs, files in os.walk('.'):
        # Remove excluded directories from the traversal
        dirs[:] = [d for d in dirs if d not in exclude_dirs]

        for filename in files:
            filepath = os.path.join(root, filename)

            # Check file size
            try:
                file_size = os.path.getsize(filepath)
                if file_size > 1_000_000:
                    continue
            except OSError:
                continue

            # Read file and check for NUL bytes
            try:
                with open(filepath, 'rb') as f:
                    content = f.read()

                # Skip files with NUL bytes
                if b'\x00' in content:
                    continue

                # Decode as UTF-8 with errors='ignore'
                text = content.decode('utf-8', errors='ignore')

                # Count occurrences of 'pytest' (case-insensitive)
                count = text.lower().count('pytest')
                total_count += count

            except (OSError, IOError):
                continue

    return total_count

if __name__ == '__main__':
    count = count_pytest_occurrences()
    print(f"Total 'pytest' occurrences: {count}")

    # Write to answer.json
    import json
    with open('eval_artifacts/answer.json', 'w') as f:
        json.dump({"token_pytest_count": count}, f)
