import os

def find_largest_file(root_dir, exclude_dirs=None):
    if exclude_dirs is None:
        exclude_dirs = ['.git']

    largest_file = None
    largest_size = 0

    for dirpath, dirnames, filenames in os.walk(root_dir):
        # Remove excluded directories from the traversal
        dirnames[:] = [d for d in dirnames if d not in exclude_dirs]

        for filename in filenames:
            filepath = os.path.join(dirpath, filename)
            try:
                size = os.path.getsize(filepath)
                if size > largest_size:
                    largest_size = size
                    largest_file = filepath
            except (OSError, IOError):
                continue

    return largest_file, largest_size

largest_file, largest_size = find_largest_file('.')
if largest_file:
    # Get relative path
    rel_path = os.path.relpath(largest_file, '.')
    print(f'Path: {rel_path}')
    print(f'Size: {largest_size}')
else:
    print('No files found')
