import os
root='.'
max_path=None
max_size=-1
for dirpath, dirnames, filenames in os.walk(root):
    if '.git' in dirnames:
        dirnames.remove('.git')
    for fn in filenames:
        path=os.path.join(dirpath, fn)
        try:
            st=os.stat(path)
        except OSError:
            continue
        if st.st_size>max_size:
            max_size=st.st_size
            max_path=path
print(max_path, max_size)
