python3 - <<'PY'
import os
max_path=None
max_size=-1
for root, dirs, files in os.walk('.'):
    # skip .git
    if '.git' in dirs:
        dirs.remove('.git')
    for f in files:
        p=os.path.join(root,f)
        try:
            st=os.stat(p)
        except FileNotFoundError:
            continue
        if st.st_size>max_size:
            max_size=st.st_size
            max_path=p
print(max_path, max_size)
PY
