import os, json

def count_pytest(repo_root='.'):
    total = 0
    exclude_dirs = {'.git', 'eval_artifacts'}
    for root, dirs, files in os.walk(repo_root):
        # prune excluded dirs
        dirs[:] = [d for d in dirs if d not in exclude_dirs]
        for fn in files:
            path = os.path.join(root, fn)
            try:
                st = os.stat(path)
            except OSError:
                continue
            if st.st_size > 1_000_000:
                continue
            try:
                with open(path, 'rb') as f:
                    data = f.read()
            except OSError:
                continue
            if b'\x00' in data:
                continue
            text = data.decode('utf-8', errors='ignore')
            total += text.lower().count('pytest')
    return total

cnt = count_pytest('.')
os.makedirs('eval_artifacts', exist_ok=True)
with open('eval_artifacts/answer.json', 'w', encoding='utf-8') as f:
    json.dump({'token_pytest_count': cnt}, f)
