import os, json

def count_pytest(root='.'):
    total = 0
    for dirpath, dirnames, filenames in os.walk(root):
        # exclude directories
        dirnames[:] = [d for d in dirnames if d not in {'.git', 'eval_artifacts'}]
        for fn in filenames:
            path = os.path.join(dirpath, fn)
            try:
                st = os.stat(path)
            except OSError:
                continue
            if st.st_size > 1_000_000:
                continue
            try:
                with open(path, 'rb') as f:
                    data = f.read()
            except OSError:
                continue
            if b'\x00' in data:
                continue
            text = data.decode('utf-8', errors='ignore')
            total += text.lower().count('pytest')
    return total

count = count_pytest('.')
os.makedirs('eval_artifacts', exist_ok=True)
with open('eval_artifacts/answer.json', 'w', encoding='utf-8') as f:
    json.dump({'token_pytest_count': count}, f)
PY"}

