{"command":"python3 - <<'PY'
import os, json
repo_root = os.getcwd()
exclude_dirs = {'.git', 'eval_artifacts'}
max_size = 1_000_000
needle = 'pytest'
count = 0

for root, dirs, files in os.walk(repo_root):
    # modify dirs in-place to skip excluded
    dirs[:] = [d for d in dirs if d not in exclude_dirs]
    for fn in files:
        path = os.path.join(root, fn)
        try:
            st = os.stat(path)
        except OSError:
            continue
        if st.st_size > max_size:
            continue
        try:
            with open(path, 'rb') as f:
                data = f.read()
        except OSError:
            continue
        if b'\x00' in data:
            continue
        text = data.decode('utf-8', errors='ignore')
        count += text.lower().count(needle)

out_path = os.path.join(repo_root, 'eval_artifacts', 'answer.json')
os.makedirs(os.path.dirname(out_path), exist_ok=True)
with open(out_path, 'w', encoding='utf-8') as f:
    json.dump({'token_pytest_count': count}, f)
PY"}

