#!/usr/bin/env python3
import os
import json

EXCLUDE_DIRS = {'.git', 'eval_artifacts'}
MAX_FILE_SIZE = 1_000_000

def should_skip_dir(dirname):
    return dirname in EXCLUDE_DIRS

def count_pytest_in_file(filepath):
    try:
        # Check file size
        size = os.path.getsize(filepath)
        if size > MAX_FILE_SIZE:
            return 0

        # Read and check for NUL byte
        with open(filepath, 'rb') as f:
            content_bytes = f.read()

        # Skip files with NUL byte
        if b'\x00' in content_bytes:
            return 0

        # Decode as UTF-8 with errors='ignore'
        text = content_bytes.decode('utf-8', errors='ignore')

        # Count 'pytest' case-insensitively
        return text.lower().count('pytest')
    except (IOError, OSError, UnicodeDecodeError):
        return 0

def main():
    total_count = 0
    repo_root = '.'

    for root, dirs, files in os.walk(repo_root):
        # Modify dirs in-place to exclude certain directories
        dirs[:] = [d for d in dirs if not should_skip_dir(d)]

        for filename in files:
            filepath = os.path.join(root, filename)
            count = count_pytest_in_file(filepath)
            total_count += count

    result = {"token_pytest_count": total_count}

    with open('eval_artifacts/answer.json', 'w') as f:
        json.dump(result, f)

    print(f"Total 'pytest' count: {total_count}")
    print(f"Result written to eval_artifacts/answer.json")

if __name__ == '__main__':
    main()
