import os
import json

def count_yaml_files(root_dir):
    yaml_count = 0
    for dirpath, dirnames, filenames in os.walk(root_dir):
        # Skip .git directory
        if '.git' in dirnames:
            dirnames.remove('.git')

        for filename in filenames:
            if filename.endswith('.yaml') or filename.endswith('.yml'):
                yaml_count += 1

    return yaml_count

# Count YAML files in the repo root
repo_root = '.'
yaml_file_count = count_yaml_files(repo_root)

# Write result to eval_artifacts/answer.json
result = {"yaml_file_count": yaml_file_count}
with open('eval_artifacts/answer.json', 'w') as f:
    json.dump(result, f, indent=2)

