import os
import sys

def find_largest_file(directory):
    largest_size = 0
    largest_path = ""

    for root, dirs, files in os.walk(directory):
        # Skip .git directory
        if '.git' in dirs:
            dirs.remove('.git')

        for file in files:
            file_path = os.path.join(root, file)
            try:
                file_size = os.path.getsize(file_path)
                if file_size > largest_size:
                    largest_size = file_size
                    largest_path = file_path
            except OSError:
                continue

    return largest_path, largest_size

directory = "."
largest_path, largest_size = find_largest_file(directory)

# Remove the leading './' from the path
if largest_path.startswith('./'):
    largest_path = largest_path[2:]

print(f"{largest_path},{largest_size}")

