import os
import sys

def find_largest_file(directory):
    largest_size = 0
    largest_path = ""

    for root, dirs, files in os.walk(directory):
        # Skip .git directory
        if '.git' in dirs:
            dirs.remove('.git')
        # Skip eval_artifacts directory
        if 'eval_artifacts' in dirs:
            dirs.remove('eval_artifacts')

        for file in files:
            file_path = os.path.join(root, file)
            try:
                file_size = os.path.getsize(file_path)
                if file_size > largest_size:
                    largest_size = file_size
                    largest_path = file_path
            except OSError:
                continue

    return largest_path, largest_size

if __name__ == "__main__":
    repo_root = "."
    largest_path, largest_size = find_largest_file(repo_root)
    if largest_path:
        # Convert to relative path
        relative_path = os.path.relpath(largest_path, repo_root)
        print(f"{relative_path}|{largest_size}")
    else:
        print("No files found")

import os
import json

def find_largest_file(root_dir):
    largest_size = 0
    largest_path = ""

    for dirpath, dirnames, filenames in os.walk(root_dir):
        # Skip .git directory
        if '.git' in dirpath:
            continue

        for filename in filenames:
            filepath = os.path.join(dirpath, filename)
            try:
                file_size = os.path.getsize(filepath)
                if file_size > largest_size:
                    largest_size = file_size
                    largest_path = filepath
            except OSError:
                continue

    return largest_path, largest_size

# Get the root directory (current directory)
root_dir = "."
largest_path, largest_size = find_largest_file(root_dir)

# Write result to eval_artifacts/answer.json
result = {
    "largest_file": {
        "path": largest_path,
        "size": largest_size
    }
}

with open('eval_artifacts/answer.json', 'w') as f:
    json.dump(result, f, indent=2)
